mapper.select:
  	select count(*) from video v left join course c on v.course_id = c.id left join speaker s on v.speaker_id = s.id
	 
  		
  			and v.video_title like '%' #{videoTitle} '%'
  		
  		
  			and v.course_id = #{videoCourse}
  		
  		
  			and v.speaker_id = #{videoSpeaker}

select v.*,c.course_name,s.speaker_name from video v left join course c on v.course_id=c.id left join speaker s on v.speaker_id=s.id
	
		
			and v.video_title like '%' #{videoTitle} '%'
		
		
			and v.course_id = #{videoCourse}
		
		
			and v.speaker_id = #{videoSpeaker}
		
	
	limit #{page},5
